module.function:



this.pinsSimulator = shell.delegate("addSimulatorPart", {
	header : { 
		label : "Interfaces", 
		name : "Input&Output", 
		iconVariant : PinsSimulators.SENSOR_KNOB 
	},
	axes : [
		new PinsSimulators.AnalogInputAxisDescription(
			{
				valueLabel : "Celsius",
				valueID : "temperatureValue",
				defaultControl : PinsSimulators.SLIDER,
				minValue : -30,
				maxValue : 100,
				value : 0
			}
		),
		new PinsSimulators.AnalogInputAxisDescription(
			{
				valueLabel : "Input Voltage",
				valueID : "wetValue",
				defaultControl : PinsSimulators.SLIDER,
			}
		),
		new PinsSimulators.AnalogInputAxisDescription(
			{
				valueLabel : "lx",
				valueID : "LuminosityValue",
				defaultControl : PinsSimulators.SLIDER,
				minValue : -30,
				maxValue : 100,
				value : 0
			}
		),
		new PinsSimulators.DigitalOutputAxisDescription(
			{
				valueLabel : "Watering",
				valueID : "wateringValue"
			}
		),
		new PinsSimulators.DigitalOutputAxisDescription(
			{
				valueLabel : "LED",
				valueID : "ledValue"
			}
		),
	]
});

module.script:
exports.pins = {
	wetSensor: { type: "A2D" },
	i2cSensors: {type: "I2C", address: 0x48},
	watering: {type: "Digital", direction: "output"},
	led: { type: "Digital", direction: "output" }
};
